import React from 'react'
const Movies = ({ movies }) => {
const imgurl = 'https://image.tmdb.org/t/p/w500/'
  return (
    <div>
      <center><h1>Movies List</h1></center>
      {movies.map((movie) => (


        <div class="card">
        <img src={imgurl + movie.poster_path} class="card-img-top" alt={movie.title}/>
          <div class="card-body">
            <h5 class="card-title">{movie.title}</h5>
            <h6 class="card-subtitle mb-2 text-muted">{movie.vote_average}</h6>
            <p class="card-text">{movie.overview}</p>
          </div>
        </div>
      ))}
    </div>
  )
};

export default Movies
