import React, {useState, useEffect} from 'react';
import '../App.css';
import {Link} from 'react-router-dom'
const imgurl = 'https://image.tmdb.org/t/p/w500/'

function Trending() {

  const cardStyle = {

    maxWidht:'50%'
};

useEffect(() => {
  fetchMovies();
},[]);

const [movies,setMovies] =useState([]);

  const fetchMovies = async () => {
    const data = await fetch('https://api.themoviedb.org/3/movie/popular?api_key=e38856e23f17467992d3ed681bef9ce0&language=en-US&page=1');

    const movies = await data.json()
    console.log(movies)
    setMovies(movies.results)
  };


  return (
    <div>

      {movies.map((movie) => (
        <div class="card mb-3" style={cardStyle}>
      <div class="row no-gutters">
        <div class="col-md-4">
        <img src={imgurl + movie.poster_path} class="card-img" alt={movie.title}/>
        </div>
        <div class="col-md-8">
          <div class="card-body">
            <h5 class="card-title">
            <Link to={`/${movie.id}`}>
            {movie.title}
            </Link>
            </h5>
            <p class="card-text">{movie.overview}</p>
            <p class="card-text"><small class="text-muted">{movie.vote_average}</small></p>
          </div>
        </div>
      </div>
    </div>
      ))}
    </div>
  )
};
export default Trending
