import React, {useState, useEffect} from 'react';
import '../App.css';
import { AST_Unary } from 'terser';

const imgurl = 'https://image.tmdb.org/t/p/w500/'

function Movie({match}) {

useEffect(() => {
  fetchMovie();
},[]);

const [movie,setMovie] =useState({});

  const fetchMovie = async () => {
      const fetchMovie = await fetch(`https://api.themoviedb.org/3/movie/${match.params.id}?api_key=e38856e23f17467992d3ed681bef9ce0&language=pt-BR`);
      const movie = await fetchMovie.json();
  };


  return (
    <div>
        <div class="card mb-3" style={{width: "50%"}}>
      <div class="row no-gutters">
        <div class="col-md-4">
        <img src={imgurl + movie.poster_path} class="card-img" alt={movie.title}/>
        </div>
        <div class="col-md-8">
          <div class="card-body">
            <h5 class="card-title">
            {movie.title}
            </h5>
            <p class="card-text">{movie.overview}</p>
            <p class="card-text"><small class="text-muted">{movie.vote_average}</small></p>
          </div>
        </div>
      </div>
    </div>
      )}
    </div>
  )
};

export default Movie
