import React, { Component } from 'react';
import Movies from './components/movies';

class App extends Component {

  state = {
    movies: []
  }
  componentDidMount() {
    fetch('https://api.themoviedb.org/3/movie/popular?api_key=<<e38856e23f17467992d3ed681bef9ce0>>&language=en-US&page=1')
    .then(res => res.json())
    .then((data) => {
      this.setState({ movies: data })
    })
    .catch(console.log)
  }
  render() {
    return (
      <Movies movies={this.state.movies} />
    )
  }
}

export default App
